
public class swapping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a,b,c;
		a=100;
		b=200;
		System.out.println("a =" +a);
		System.out.println("b ="  +b);
		System.out.println("\n Swapped numbers are :  ");
		c = b;
		b = a;
		a = c;
		System.out.println("a =" +a);
		System.out.println("b ="  +b);
		

	}

}
